import React, { useState, useEffect } from 'react'
import { Button, Col, DatePicker, Divider, Row, Table } from 'antd'
import { logdata_request } from '../../api';
import { DataSortTime, GetDateTime, LogDataMappingTime, SetDateRange, TableColumbTimeAsRow } from '../../DataRequest/LogDataRequestWrap';
import { C3Model } from '../../DataRequest/DataModel';
import { Page, Text, View, Document, StyleSheet, PDFViewer } from '@react-pdf/renderer';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

// Create styles
const styles = StyleSheet.create({
    page: {
        flexDirection: 'row',
        backgroundColor: '#E4E4E4'
    },
    section: {
        margin: 10,
        padding: 10,
        flexGrow: 1
    }
});

let LogValue = {
    "LogC3": [],
    "LogG2": [],
}

const Columb = TableColumbTimeAsRow()

const Line1Report = () => {

    const [state, setState] = useState({ count: 0 });


    const TestGet = () => {
        let DateTimeRange = GetDateTime(1)
        UpdateTableData([], DateTimeRange[0], DateTimeRange[1], 'Polymerlink', 'C3', '1')
    }


    const UpdateTableData = async (Parameter, StartTime, EndTime, Site, Line, Page) => {
        const response = await logdata_request(Parameter, StartTime, EndTime, Site, Line, Page)
        //console.log(response);
        let da = DataSortTime(response)
        LogValue['LogC3'] = LogDataMappingTime(C3Model, da)
        //console.log(LogValue['LogC3']);
        setState(({ count }) => ({ count: count + 1 }));

    }

    const SetTimeRange = (value) => {
        const DateRange = SetDateRange(value)
        UpdateTableData([], DateRange[0], DateRange[1], 'Polymerlink', 'C3', '1')
    }

    const convertCanvas = () => {
        const input = document.getElementById("capture");

        html2canvas(input, { scale: 6 })
            .then((canvas) => {
                const imgData = canvas.toDataURL("image/jpeg");
                const pdf = new jsPDF({
                    orientation: 'landscape',
                    type: 'a4'
                });
                //const imgProps = pdf.getImageProperties(imgData);
                const pdfWidth = pdf.internal.pageSize.getWidth();
                const pdfHeight = pdf.internal.pageSize.getHeight();
                pdf.addImage(imgData, 'SVG', 0, 0, pdfWidth, pdfHeight);
                pdf.save('download.pdf');
            });
    }


    return (
        <div>
            <DatePicker onChange={SetTimeRange} />
            <Button onClick={TestGet}> test </Button>
            <Button onClick={convertCanvas}> export </Button>

            <div id='capture'>
                <Table columns={Columb} dataSource={LogValue['LogC3']} bordered  pagination={false} size="small" />

            </div>
        </div>
    )


}

export default Line1Report
