import React, { useEffect, useState } from 'react'
import { ReadJob, RealtimeData_Request } from '../../api'
import { FilterOutEndedJob, GetDateTime, ReportSort, TableColumbJob, TableColumbJobHome, DateDisplay } from '../../DataRequest/LogDataRequestWrap';
import moment from 'moment';
import { C3Model, G2Model, JobModel } from '../../DataRequest/DataModel';
import { Button, Col, Row, Table, Typography, Statistic, Card, Descriptions, PageHeader, Tag } from 'antd';
import RealTimeTableWrapper from '../Realtime/RealTimeTableWrapper';
import { DataSortRT } from '../../DataRequest/RealTimeRequestWrap';
import { ArrowUpOutlined, ArrowDownOutlined, DashboardOutlined } from '@ant-design/icons';
import './home.css'

let LogValue = {
    "LogC3": [],
    "LogG2": [],
}

let ReportList = {
    "C3ReportList": [],
    "G2ReportList": [],
    'All': []
}

let JobDetailRecord = {
    datetimeStart: '',
    datetimeEnd: '',
    site: '',
    line: '',
    BufferName: ''
}

let ReportInfoG2 = {}

let BlankPage = 1, ClickTime

let RealTimeValue = {
    "C3Table": [],
    "G2Table": [],
    "C3Graph": [],
    "G2Graph": [],
}

const data = [
    {
        DateTime: '2022/5/20',
        Time: '00:00:00',
        Line50: 32,
        Line1: 45,
        Line2: 64,
        Line1T: 34,
    },
    {
        DateTime: '2022/5/22',
        Time: '00:00:00',
        Line50: 65,
        Line1: 43,
        Line2: 65,
        Line1T: 87,
    },
    {
        DateTime: '2022/5/21',
        Time: '00:00:00',
        Line50: 453,
        Line1: 65,
        Line2: 90,
        Line1T: 46,
    },
    {
        DateTime: '2022/5/24',
        Time: '00:00:00',
        Line50: 543,
        Line1: 87,
        Line2: 765,
        Line1T: 0,
    },
    {
        DateTime: '2022/5/23',
        Time: '00:00:00',
        Line50: 32,
        Line1: 45,
        Line2: 64,
        Line1T: 34,
    },
    {
        DateTime: '2022/5/20',
        Time: '01:00:00',
        Line50: 32,
        Line1: 45,
        Line2: 64,
        Line1T: 34,
    },
    {
        DateTime: '2022/5/22',
        Time: '01:00:00',
        Line50: 65,
        Line1: 43,
        Line2: 65,
        Line1T: 87,
    },
    {
        DateTime: '2022/5/21',
        Time: '01:00:00',
        Line50: 453,
        Line1: 65,
        Line2: 90,
        Line1T: 46,
    },
    {
        DateTime: '2022/5/24',
        Time: '01:00:00',
        Line50: 543,
        Line1: 87,
        Line2: 765,
        Line1T: 0,
    },
    {
        DateTime: '2022/5/23',
        Time: '01:00:00',
        Line50: 32,
        Line1: 45,
        Line2: 64,
        Line1T: 34,
    },
];

const Home = () => {

    const { Title } = Typography

    const [state, setState] = useState({ count: 0 });
    const [sortedInfo, setSortedInfo] = useState({});

    const handleChange = (pagination, filters, sorter) => {

        // var dateMomentObject = moment("23/03/2022", "DD/MM/YYYY"); // 1st argument - string, 2nd argument - format
        // var dateObject = dateMomentObject.toDate(); // convert moment.js object to Date object

        // console.log(dateObject)


        console.log(moment("23/03/2022", "DD/MM/YYYY").toDate())

        console.log('Various parameters', pagination, filters, sorter);
        // setFilteredInfo(filters);
        setSortedInfo(sorter);
    };

    const DataRequest = async (label, site, page, line, HMIName, TableName) => {
        const response = await RealtimeData_Request(label, site, page, line)
        RealTimeValue[TableName] = DataSortRT(response)
        RealTimeValue[HMIName] = RealTimeValue[TableName]
    }

    const RenderNewData = () => {

        DataRequest([], 'Polymerlink', BlankPage, 'C3', 'C3HMI', "C3Table")
        DataRequest([], 'Polymerlink', BlankPage, 'G2', 'G2HMI', "G2Table")

        setState(({ count }) => ({ count: count + 1 }));
    }


    useEffect(() => {

        RenderNewData()
        time()

        // const interval = setInterval(() => {
        //     RenderNewData()
        // }, 3000)

        return () => {
            // clearInterval(interval)
            console.log('Unmount Effect Line1RT');
        }

    }, [])


    const RequestJob = async (info, datetimeStart, datetimeEnd, site, line, BufferName) => {
        const response = await ReadJob(info, datetimeStart, datetimeEnd, site, line[0])
        filter(response, BufferName[0])
        const response2 = await ReadJob(info, datetimeStart, datetimeEnd, site, line[1])
        filter(response2, BufferName[1])

        console.log();

        ReportList['All'] = []

        ReportList[BufferName[0]].forEach(element => {
            ReportList['All'].push(element)
        });

        ReportList[BufferName[1]].forEach(element => {
            ReportList['All'].push(element)
        });

        console.log(ReportList['All']);


        setState(({ count }) => ({ count: count + 1 }));
    }

    const filter = (response, BufferName) => {
        console.log(response)
        let data = ReportSort(response)
        console.log(data)
        let OngoingJob = FilterOutEndedJob(data)
        console.log(OngoingJob)
        ReportList[BufferName] = OngoingJob
    }



    const time = (line, BufferName) => {
        const timetange = GetDateTime(2, 20)
        RequestJob([], timetange[0], timetange[1], 'Polymerlink', ['G2', 'C3'], ["G2ReportList", "C3ReportList"])
    }


    const ColumbJob = TableColumbJobHome(JobModel, 'G2', BlankPage, 'Log')


    const LogColumb = [
        {
            title: 'DateTime',
            width: 70,
            dataIndex: 'DateTime',
            key: 'DateTime',
            align: 'center',

            //  dateMomentObject = moment("23/03/2022", "DD/MM/YYYY"); // 1st argument - string, 2nd argument - format
            //  dateObject = dateMomentObject.toDate(); // convert moment.js object to Date object
            sorter: (a, b) => new Date(a.DateTime) - new Date(b.DateTime),

            // sorter: (a, b) => moment(a.date, "DD/MM/YYYY").toDate() - moment(b.date, "DD/MM/YYYY").toDate(),
            // sorter: (a, b) => new Date(a.DateTime) - new Date(b.DateTime),
            sortOrder: sortedInfo.columnKey === 'DateTime' ? sortedInfo.order : null,
            ellipsis: true,
        },

        {
            title: 'Time',
            width: 70,
            dataIndex: 'Time',
            key: 'Time',
            align: 'center',

            sorter: (a, b) => moment(a.Time).unix() - moment(b.Time).unix(),
            sortOrder: sortedInfo.columnKey === 'Time' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '50 KG',
            width: 70,
            dataIndex: 'Line50',
            key: 'Line50',
            align: 'center',

            sorter: (a, b) => a.Line50 - b.Line50,
            sortOrder: sortedInfo.columnKey === 'Line50' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '1 KG',
            width: 70,
            dataIndex: 'Line1',
            key: 'Line1',
            align: 'center',
        },
        {
            title: '2 KG',
            width: 70,
            dataIndex: 'Line2',
            key: 'Line2',
            align: 'center',
        },
        {
            title: '1 TON',
            width: 70,
            dataIndex: 'Line1T',
            key: 'Line1T',
            align: 'center',
        },

    ]


    const comparedate = () => {
        const ii = new Date()
        const NowTime = moment(ii).format('DD-MM-yyyy HH:mm:ss');

        let dat1 = '11-12-2021 01:00:00'
        let dat2 = '01-12-2021 23:00:00'

        var date1 = new Date(dat1);
        var date2 = new Date(dat2);

        console.log(NowTime);

        date2 > date1 ? console.log('yes') : console.log('no');

    }



    return (
        <div >
