        Label: '2 Kg'
    },
    {
        DateTime: '2022/5/23 01:40:00',
        Id: 2,
        Weight: 'TBA',
        Location: 'MSMSGBULOH',
        Label: '1 Kg'
    },
];

const Home = () => {

    const { Title } = Typography

    const [state, setState] = useState({ count: 0 });
    const [sortedInfo, setSortedInfo] = useState({});
    const [sortedInfo2, setSortedInfo2] = useState({});

    const DailyLogRequest = async () => {
        let response = await MSM_logdata_request(RequestDataOption, RequestDateRange[0], RequestDateRange[1])
        //  let responseSorted = LogDataMappingTimeMSM(MSMTable,response)   
        responseSorted = LogDataMappingNameMSM(MSMTable, DataSortTimeMSM(response))
        console.log(responseSorted);
        setState(({ count }) => ({ count: count + 1 }));

    }


    const handleChangeOverall = (pagination, filters, sorter) => {
        console.log('Various parameters', pagination, filters, sorter);
        setSortedInfo(sorter);
    };

    const handleChangePertime = (pagination, filters, sorter) => {
        console.log('Various parameters', pagination, filters, sorter);
        setSortedInfo2(sorter);
    };

    const DataRequest = async (label, site, page, line, HMIName, TableName) => {
        const response = await RealtimeData_Request(label, site, page, line)
        RealTimeValue[TableName] = DataSortRT(response)
        RealTimeValue[HMIName] = RealTimeValue[TableName]
    }

    const RenderNewData = () => {

        DataRequest([], 'Polymerlink', BlankPage, 'C3', 'C3HMI', "C3Table")
        DataRequest([], 'Polymerlink', BlankPage, 'G2', 'G2HMI', "G2Table")

        setState(({ count }) => ({ count: count + 1 }));
    }


    useEffect(() => {

        RenderNewData()

        // const interval = setInterval(() => {
        //     RenderNewData()
        // }, 3000)

        return () => {
            // clearInterval(interval)
            console.log('Unmount Effect Line1RT');
        }

    }, [])


    const LogColumb = [
        {
            title: 'DateTime',
            width: 70,
            dataIndex: 'DateTime',
            key: 'DateTime',
            align: 'center',

            sorter: (a, b) =>
                new Date(moment(a.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")) -
                new Date(moment(b.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")),

            sortOrder: sortedInfo.columnKey === 'DateTime' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '50 KG',
            width: 70,
            dataIndex: 'Line50',
            key: 'Line50',
            align: 'center',

            sorter: (a, b) => a.Line50 - b.Line50,
            sortOrder: sortedInfo.columnKey === 'Line50' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '1 KG',
            width: 70,
            dataIndex: 'Line1',
            key: 'Line1',
            align: 'center',

            sorter: (a, b) => a.Line1 - b.Line1,
            sortOrder: sortedInfo.columnKey === 'Line1' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '2 KG',
            width: 70,
            dataIndex: 'Line2',
            key: 'Line2',
            align: 'center',

            sorter: (a, b) => a.Line2 - b.Line2,
            sortOrder: sortedInfo.columnKey === 'Line2' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '1 TON',
            width: 70,
            dataIndex: 'Line1T',
            key: 'Line1T',
            align: 'center',

            sorter: (a, b) => a.Line1T - b.Line1T,
            sortOrder: sortedInfo.columnKey === 'Line1T' ? sortedInfo.order : null,
            ellipsis: true,
        },

    ]

    const LogColumbPerTime = [
        {
            title: 'DateTime',
            width: 50,
            dataIndex: 'DateTime',
            key: 'DateTime',
            align: 'center',

            sorter: (a, b) =>
                new Date(moment(a.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")) -
                new Date(moment(b.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")),

            sortOrder: sortedInfo2.columnKey === 'DateTime' ? sortedInfo2.order : null,
            ellipsis: true,
        },
        {
            title: 'Id Unit',
            width: 40,
            dataIndex: 'Id',
            key: 'Id',
            align: 'center',

            sorter: (a, b) => a.Id - b.Id,
            sortOrder: sortedInfo2.columnKey === 'Id' ? sortedInfo2.order : null,
            ellipsis: true,
        },
        {
            title: 'Label',
            width: 40,
            dataIndex: 'Label',
            key: 'Label',
            align: 'center',

            filters: [
                {
                    text: '50 Kg',
                    value: '50 Kg',
                },
                {
                    text: '1 Kg',
                    value: '1 Kg',
                },
                {
                    text: '2 Kg',
                    value: '2 Kg',
                },
                {
                    text: '1 Ton',
                    value: '1 Ton',
                },
            ],
            onFilter: (value, record) => record.Label.indexOf(value) === 0,
        },
        {
            title: 'Weight',
            width: 40,
            dataIndex: 'Weight',
            key: 'Weight',
            align: 'center',

            sorter: (a, b) => a.Weight - b.Weight,
            sortOrder: sortedInfo2.columnKey === 'Weight' ? sortedInfo2.order : null,
            ellipsis: true,
        },
        {
            title: 'Location',
            width: 40,
            dataIndex: 'Location',
            key: 'Location',
            align: 'center',

            sorter: (a, b) => a.Location - b.Location,
            sortOrder: sortedInfo2.columnKey === 'Location' ? sortedInfo2.order : null,
            ellipsis: true,
        },
    ]

    return (
        <div >

