                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 50 KG</h2>

                                {/* <Title level={3}>Packaging Line 50 KG</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 1 KG</h2>
                                {/* <Title level={3}>Packaging Line 1 KG</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 2 KG</h2>
                                {/* <Title level={3}>Packaging Line 2 KG</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 1 TON</h2>
                                {/* <Title level={3}>Packaging Line 1 TON</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                    </Row>
                </Card>
            </div>

            <div style={{ marginTop: '16px' }}>
                <Row gutter={16}>
                    <Col span={12}>
                        <Card style={{ height: '540px', backgroundColor: 'AppWorkspace', border: '3px outset' }} >
