                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                                <Col span={12}>
