                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                                <Col span={12}>
                                    <Card title={<Title level={4} style={{ fontFamily: 'cursive' }}>2 Kg</Title>}>

                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                                <Col span={12}>
                                    <Card title={<Title level={4} style={{ fontFamily: 'cursive' }}>1 Ton</Title>}>

                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                            </Row>
                        </Card>

                    </Col>

                    <Col span={12}>

                        <Card style={{ height: '540px', backgroundColor: 'AppWorkspace', border: '3px outset' }} >

                            <Title level={1} style={{ fontWeight: 'bold' }}> Counter Log  </Title>

                            <Table bordered columns={LogColumbPerTime} dataSource={data2} scroll={{ x: 600, y: 350 }} pagination={false} size="small" onChange={handleChangePertime} />
                        </Card>

                    </Col>


                </Row>
            </div>

            <div style={{ marginTop: '16px' }}>

                <Card style={{ backgroundColor: 'AppWorkspace', border: '3px outset' }} >

                    <Title level={1} style={{ fontWeight: 'bold' }} > Daily Log  </Title>
                    <Table columns={LogColumb} dataSource={data} bordered scroll={{ x: 1000, y: 500 }} pagination={false} size="small" onChange={handleChangeOverall} />
                </Card>

            </div>




            {/* <Button onClick={time}> test </Button>
            <Button onClick={comparedate}> compare </Button>
             */}


        </div>
    )
}

