    },
    {
        DateTime: '30/5/2022',
        type: 'Line1kg',
        Value: 876,
    },


    {
        DateTime: '24/5/2022',
        type: 'Line2kg',
        Value: 543,
    },
    {
        DateTime: '25/5/2022',
