        Value: 23,
    },
    {
        DateTime: '26/5/2022',
        type: 'Line1Ton',
        Value: 64,
    },
    {
        DateTime: '27/5/2022',
        type: 'Line1Ton',
        Value: 76,
    },
    {
        DateTime: '28/5/2022',
