import jsPDF from "jspdf";


function createHeaders(keys) {
    var result = [];
    for (var i = 0; i < keys.length; i += 1) {
        result.push({
            id: keys[i],
            name: keys[i],
            prompt: keys[i],
            width: 75,
            align: "center",
            padding: 3,
        });
    }
    return result;
}

export const TimeColumbPDF = () => {

    let TimeRow = ['Parameter']

    for (let i = 0; i < 24; i++) {
        let n
        i < 10 ? n = `0${i}` : n = i
        TimeRow.push(`${n}:00`)
    }

    return TimeRow
}

export const NameColumbPDF = (Model) => {

   const Label = Object.keys(Model)
   Label.pop()
   Label.pop()
   Label.pop()

   Label.unshift("Time")
   console.log(Label);
    return Label
}



export const GeneratePDFC3 = (headerArray, DataArray) => {

    var headers = createHeaders(headerArray)

    var doc = new jsPDF({ putOnlyUsedFonts: true, orientation: "l", unit: "px" });
    doc.setFontSize(20)
    doc.text("Extruder Operating Sheet(82mm)", 10, 20,{});
    doc.setFontSize(15)
    doc.text("Job No.", 10, 40,{});
    doc.table(5, 60, DataArray, headers, { autoSize: true, fontSize: 9, padding: 3 });
    doc.save("a4.pdf")
}




export const GeneratePDFG2 = (headerArray, DataArray) => {

    var headers = createHeaders(headerArray)

    var doc = new jsPDF({ putOnlyUsedFonts: true, orientation: "l", unit: "px" });
    doc.setFontSize(20)
    doc.text("Extruder Operating Sheet(82mm)", 10, 20,{});
    doc.setFontSize(15)
    doc.text("Job No.", 10, 40,{});
    doc.table(5, 60, DataArray, headers, { autoSize: true, fontSize: 9, padding:3 });
    doc.save("a4.pdf")
}
