    LogDataMappingNameMSM, ConvertMonentToDateTime, DateDisplay, EventSortTimeMSM } from '../../DataRequest/LogDataRequestWrap';
import moment from 'moment';
import { MSMTable } from '../../DataRequest/DataModel';
import { Button, Col, Row, Table, Typography, Statistic, Card, DatePicker, Select } from 'antd';
import RealTimeTableWrapper from '../Realtime/RealTimeTableWrapper';
import { DataSortRT } from '../../DataRequest/RealTimeRequestWrap';
import { ArrowUpOutlined, ArrowDownOutlined, DashboardOutlined } from '@ant-design/icons';
import './home.css'
import { CSVLink, CSVDownload } from "react-csv";


let DTinitial = GetDateTime(0, 1)  // get from yesterday 00:00 to today 00:00
let DTinitial2 = GetDateTime(7, 1)
let RequestDataOption = ['Line50kg', 'Line1kg', 'Line2kg', 'Line1Ton']
let RequestDateRange = [DTinitial[0], DTinitial[1]]
let DailyLogRequestRange = [DTinitial2[0], DTinitial2[1]]
let DailyLogSorted = []
let EventLogSorted = []



const headers = [
    { label: "DateTime", key: "DateTime" },
    { label: "Tag", key: "Tag" },
    { label: "IdUnit", key: "IdUnit" },
    { label: "Label", key: "Label" },
    { label: "Weight", key: "Weight" },
    { label: "Location", key: "Location" },

];



const Home = () => {

    const { Title } = Typography
    const { RangePicker } = DatePicker;
    const { Option } = Select;


    const [state, setState] = useState({ count: 0 });
    const [sortedInfo, setSortedInfo] = useState({});
    const [sortedInfo2, setSortedInfo2] = useState({});

    const DailyLogRequest = async () => {
        let response = await MSM_logdata_request(RequestDataOption, DailyLogRequestRange[0], DailyLogRequestRange[1])
        //  let DailyLogSorted = LogDataMappingTimeMSM(MSMTable,response)   
        DailyLogSorted = LogDataMappingNameMSM(MSMTable, DataSortTimeMSM(response))
        console.log(DailyLogSorted);
        setState(({ count }) => ({ count: count + 1 }));
    }

    const EvenLogRequest = async () => {
        let response = await MSM_EventLog_request(RequestDataOption, RequestDateRange[0], RequestDateRange[1])
        //  let DailyLogSorted = LogDataMappingTimeMSM(MSMTable,response)   
        EventLogSorted = EventSortTimeMSM(response)
        console.log(EventLogSorted);
        setState(({ count }) => ({ count: count + 1 }));
        }


    const handleChangeOverall = (pagination, filters, sorter) => {
        console.log('Various parameters', pagination, filters, sorter);
        setSortedInfo(sorter);
    };

    const handleChangePertime = (pagination, filters, sorter) => {
        console.log('Various parameters', pagination, filters, sorter);
        setSortedInfo2(sorter);
    };

    const ChangeDateRange = (value) => {
        // console.log(RequestDateRange);
        RequestDateRange = []
        RequestDateRange = ConvertMonentToDateTime(value)
        // console.log(RequestDateRange);
    }

    const ChangeOption = (value) => {
        console.log(`selected ${value}`);
        RequestDataOption = []
        RequestDataOption.push(value)
        // console.log(RequestDataOption);
    }



    useEffect(() => {


        // const interval = setInterval(() => {
        //     RenderNewData()
        // }, 3000)
        DailyLogRequest()



        return () => {
            // clearInterval(interval)
            console.log('Unmount Effect Line1RT');
        }

    }, [])


    const LogColumb = [
        {
            title: 'DateTime',
            width: 50,
            dataIndex: 'DateTime',
            key: 'DateTime',
            align: 'center',

            sorter: (a, b) =>
                new Date(moment(a.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")) -
                new Date(moment(b.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")),

            sortOrder: sortedInfo.columnKey === 'DateTime' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '50 KG',
            width: 35,
            dataIndex: 'Line50kg',
            key: 'Line50kg',
            align: 'center',

            sorter: (a, b) => a.Line50kg - b.Line50kg,
            sortOrder: sortedInfo.columnKey === 'Line50kg' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '1 KG',
            width: 35,
            dataIndex: 'Line1kg',
            key: 'Line1kg',
            align: 'center',

            sorter: (a, b) => a.Line1kg - b.Line1kg,
            sortOrder: sortedInfo.columnKey === 'Line1kg' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '2 KG',
            width: 35,
            dataIndex: 'Line2kg',
            key: 'Line2kg',
            align: 'center',

            sorter: (a, b) => a.Line2kg - b.Line2kg,
            sortOrder: sortedInfo.columnKey === 'Line2kg' ? sortedInfo.order : null,
            ellipsis: true,
        },
        {
            title: '1 TON',
            width: 35,
            dataIndex: 'Line1Ton',
            key: 'Line1Ton',
            align: 'center',

            sorter: (a, b) => a.Line1Ton - b.Line1Ton,
            sortOrder: sortedInfo.columnKey === 'Line1Ton' ? sortedInfo.order : null,
            ellipsis: true,
        },

    ]

    const LogColumbPerTime = [
        {
            title: 'DateTime',
            width: 40,
            dataIndex: 'DateTime',
            key: 'DateTime',
            align: 'center',

            sorter: (a, b) =>
                new Date(moment(a.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")) -
                new Date(moment(b.DateTime, "YYYY/MM/DD hh:mm:ss ").format("LLL")),

            sortOrder: sortedInfo2.columnKey === 'DateTime' ? sortedInfo2.order : null,
            ellipsis: true,
        },
        {
            title: 'Tag',
            width: 60,
            dataIndex: 'Tag',
            key: 'Tag',
            align: 'center',
        },
        {
            title: 'Id Unit',
            width: 20,
            dataIndex: 'IdUnit',
            key: 'IdUnit',
            align: 'center',

            sorter: (a, b) => a.IdUnit - b.IdUnit,
            sortOrder: sortedInfo2.columnKey === 'IdUnit' ? sortedInfo2.order : null,
            ellipsis: true,
        },
        {
            title: 'Label',
            width: 20,
            dataIndex: 'Label',
            key: 'Label',
            align: 'center',
        },
        {
            title: 'Weight',
            width: 20,
            dataIndex: 'Weight',
            key: 'Weight',
            align: 'center',
        },
        {
            title: 'Location',
            width: 20,
            dataIndex: 'Location',
            key: 'Location',
            align: 'center',
        },
    ]

    return (
        <div >

            <div style={{ marginTop: '16px' }}>
                <Card title={<h2>Total Production Capacity Counter</h2>}>
                    <Row gutter={'3'} >
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 50 KG</h2>

                                {/* <Title level={3}>Packaging Line 50 KG</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 1 KG</h2>
                                {/* <Title level={3}>Packaging Line 1 KG</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 2 KG</h2>
                                {/* <Title level={3}>Packaging Line 2 KG</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                        <Col span={6} xs={24} xl={6}>
                            <Card bordered={true} style={{ backgroundColor: 'ButtonHighlight ', border: '3px outset' }}  >
                                <h2>Packaging Line 1 TON</h2>
                                {/* <Title level={3}>Packaging Line 1 TON</Title> */}
                                <Statistic
                                    value={1128}
                                    precision={0}
                                    valueStyle={{ color: '#3f8600', fontSize: '40px', color: '#131212', fontWeight: 'bold' }}
                                    suffix="Unit"
                                />
                            </Card>
                        </Col>
                    </Row>
                </Card>
            </div>

            <div style={{ marginTop: '16px' }}>
                <Row gutter={16}>
                    <Col span={12}>
                        <Card style={{ height: '540px', backgroundColor: 'AppWorkspace', border: '3px outset' }} >
                            <Title level={1} style={{ fontWeight: 'bold' }}> Total Capacity ({DateDisplay()})  </Title>
                            <Row gutter={[16, 16]}>
                                <Col span={12}>
                                    <Card title={<Title level={4} style={{ fontFamily: 'cursive' }}>50 Kg</Title>}>

                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                                <Col span={12}>
                                    <Card title={<Title level={4} style={{ fontFamily: 'cursive' }}>1 Kg</Title>}>

                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                                <Col span={12}>
                                    <Card title={<Title level={4} style={{ fontFamily: 'cursive' }}>2 Kg</Title>}>

                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                                <Col span={12}>
                                    <Card title={<Title level={4} style={{ fontFamily: 'cursive' }}>1 Ton</Title>}>

                                        <Statistic
                                            value={1128}
                                            precision={0}
                                            valueStyle={{ color: '#084085', fontFamily: 'monospace', fontSize: '34px' }}
                                            prefix={<DashboardOutlined />}
                                            suffix="Unit"
                                        />
                                    </Card>
                                </Col>
                            </Row>
                        </Card>

                    </Col>

                    <Col span={12}>
                        <Card style={{ backgroundColor: 'AppWorkspace', border: '3px outset', minHeight:'540px' }} >

                            <Title level={1} style={{ fontWeight: 'bold' }} > Daily Log  </Title>
                            <Table columns={LogColumb} dataSource={DailyLogSorted} bordered scroll={{ x: 500, y: 385 }} pagination={false} size="small" onChange={handleChangeOverall} />
                        </Card>


                    </Col>


                </Row>
            </div>

            <div style={{ marginTop: '16px' }}>



                <Card style={{ height: '540px', backgroundColor: 'AppWorkspace', border: '3px outset' }} >

                    <Title level={1} style={{ fontWeight: 'bold' }}> Counter Log  </Title>

                    <div style={{ paddingBottom: '10px' }}>
                        <RangePicker onChange={ChangeDateRange} />

                        <Select defaultValue="Line50kg" style={{ width: 100, marginLeft: '10px' }} onChange={ChangeOption}>
                            <Option value="Line50kg">Line50kg</Option>
                            <Option value="Line1kg">Line1kg</Option>
                            <Option value="Line2kg">Line2kg</Option>
                            <Option value="Line1Ton">Line1Ton</Option>
                        </Select>

                        <Button type='primary' style={{ marginLeft: '10px' }} >
                            <CSVLink data={EventLogSorted} target="_blank" filename={"EventLog.csv"} headers={headers}>
                                Export
                            </CSVLink>
                        </Button>



                        <Button type='primary' style={{ marginLeft: '10px' }} onClick={() => { EvenLogRequest() }}>Update</Button>
                    </div>

                    <Table bordered columns={LogColumbPerTime} dataSource={EventLogSorted} scroll={{ x: 600, y: 350 }} pagination={false} size="small" onChange={handleChangePertime} />
                </Card>

            </div>




            {/* <Button onClick={time}> test </Button>
            <Button onClick={comparedate}> compare </Button>
             */}


        </div>
    )
}

export default Home
