import { Button, Col, Divider, Row, DatePicker, Card, Table, Select, Spin } from 'antd'
import React, { useState, useEffect } from 'react'
import { logdata_request, RealtimeData_Request } from '../../api'
import { C3Model, C3ModelMap, G2Model } from '../../DataRequest/DataModel'
import { ConvertMonentToDateTime, DataSortTime, GetDateTime } from '../../DataRequest/LogDataRequestWrap'
import { Line, Column } from '@antv/g2plot';
import moment from 'moment';



let DTinitial = GetDateTime(0, 1)  // get from yesterday 00:00 to today 00:00
// console.log(DTinitial);
let BlankPage = 1, DTc3 = [], DTg2 = []
let C3DTRange = [], G2DTRange = []
let MonthlyEnergyDataSet = []
let BarchartData = []
const monthNames = ["January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
];


DTc3.push(moment(DTinitial[2]))
DTc3.push(moment(DTinitial[3]))
DTg2 = DTc3


let LogValue = {
    "C3Table": [],
    "G2Table": [],
    "C3Graph": [],
    "G2Graph": [],
    "C3Selected": [],
    "G2Selected": [],
}

let LogSelect = {
    LineSelect : 'C3',
    paraname : 'Power Meter'
}

const EnergyOption = ['C3', 'G2']

const { RangePicker } = DatePicker;
const { Option } = Select;


let OptionC3 = Object.keys(C3ModelMap)
let OptionG2 = Object.keys(G2Model)

const OptionSelectorC3 = [];
const OptionSelectorG2 = [];

LogValue['C3Selected'] = [EnergyOption[0]];
LogValue['G2Selected'] = [OptionG2[0], OptionG2[1]];

OptionC3.forEach(element => {
    OptionSelectorC3.push(<Option key={element}>{element}</Option>)
});
OptionG2.forEach(element => {
    OptionSelectorG2.push(<Option key={element}>{element}</Option>)
});

let data_filterC3 = OptionC3.map(obj => {
    let cobj = []

    cobj = {
        text: obj,
        value: obj,
    }
    return cobj
})

const parseDMYhmC3 = (s) => {
    var b = s.split(/\D/);
    return new Date(b[2], b[1] - 1, b[2], b[3], b[4]);
}

const columnsC3 = [
    {
        title: 'Parameter',
        dataIndex: 'Parameter',
        filters: data_filterC3,
        filterMode: 'tree',
        filterSearch: true,
        onFilter: (value, record) => record.Parameter.includes(value),
        width: '30%',
    },
    {
        title: 'Value',
        dataIndex: 'Value',
        sorter: (a, b) => a.Value - b.Value,
    },
    {
        title: 'Date',
        dataIndex: 'date',
        sorter: (a, b) => parseDMYhmC3(a.date) - parseDMYhmC3(b.date),
    },
];



let line, stackedColumnPlot

const EnergyUsage = () => {

    const [state, setState] = useState({ count: 0 });
    const [Loading, setLoading] = useState(false);

    useEffect(() => {
        console.log('Reffect');

        BarChartInit(['Power Meter'], DTinitial[0], DTinitial[1], 'Polymerlink', 'C3', '1', "C3Table", C3ModelMap)

        stackedColumnPlot = new Column('container', {
            data: MonthlyEnergyDataSet,
            isStack: true,
            xField: 'Date',
            yField: 'value',
            seriesField: 'type',
            label: {
                position: 'middle',
                layout: [
                    { type: 'interval-adjust-position' },
                    { type: 'interval-hide-overlap' },
                    { type: 'adjust-color' },
                ],
            },
        });

        try { stackedColumnPlot.render(); }
        catch { }


        line = new Line('C3Graph', {
            data: LogValue['C3Table'],
            xField: 'date',
            yField: 'Value',
            seriesField: 'Parameter',
        })

        try { line.render() }
        catch { }

        // line2 = new Line('G2Graph', {
        //     data: LogValue['G2Table'],
        //     xField: 'date',
        //     yField: 'Value',
        //     seriesField: 'Parameter',
        // })

        // try { line2.render() }
        // catch { }

        return () => {
            line.destroy();
            stackedColumnPlot.destroy()
            // line2.destroy();

        }
    }, [])

    const UpdateTableData = async (Parameter, StartTime, EndTime, Site, Line, Page, BufferName, Model) => {
        console.log(Parameter)
        const response = await logdata_request(Parameter, StartTime, EndTime, Site, Line, Page)
        let data = DataSortTime(response)
        LogValue[BufferName] = data
        console.log(data);
        setState(({ count }) => ({ count: count + 1 }));
    }

    const BarChartInit = async (Parameter, StartTime, EndTime, Site, Line, Page, BufferName, Model) => {
        setLoading(true)
        // console.log(Parameter)
        MonthlyEnergyDataSet = []

        for (let month = 0; month < 6; month++) {
            let date = new Date();
            let firstDay = new Date(date.getFullYear(), date.getMonth() - month, 1);
            let lastDay = new Date(date.getFullYear(), date.getMonth() + 1 - month, 0);
            let dat = [], MonthlyEnergy = []


            dat.push(firstDay)
            dat.push(lastDay)

            let DateInterval = ConvertMonentToDateTime(dat)

            const response = await logdata_request(Parameter, DateInterval[0], DateInterval[1], Site, Line, Page)

            if (response.data.length > 0) {
                let length = response.data.length
                MonthlyEnergy[month] = (response.data[length - 1].fields.Value - response.data[0].fields.Value).toFixed(2)
            }

            let Energy = {
                "type": "C3",
                "value": parseFloat(MonthlyEnergy[month]),
                "Date": monthNames[firstDay.getMonth()]
            }

            MonthlyEnergyDataSet.unshift(Energy)
        }
        console.log(MonthlyEnergyDataSet);
        stackedColumnPlot.changeData(MonthlyEnergyDataSet)
        setLoading(false)
        // setState(({ count }) => ({ count: count + 1 }));
    }

    const C3LogRequest = async (DT) => {
        DTc3 = DT
        console.log(DTc3);
        C3DTRange = ConvertMonentToDateTime(DT)
        console.log(C3DTRange);

        await UpdateTableData(LogValue['C3Selected'], C3DTRange[0], C3DTRange[1], 'Polymerlink', 'C3', BlankPage, "C3Table", C3ModelMap)
        console.log(LogValue["C3Table"]);
        line.changeData(LogValue["C3Table"])
    }

    // const G2LogRequest = async (DT) => {
    //     DTg2 = DT
    //     G2DTRange = ConvertMonentToDateTime(DT)
    //     await UpdateTableData(LogValue['G2Selected'], G2DTRange[0], G2DTRange[1], 'Polymerlink', 'G2', BlankPage, "G2Table", G2Model)
    //     line2.changeData(LogValue["G2Table"])
    // }

    const ChangeC3Select = (value) => {

        let selectline

        if(value === 'C3'){
            LogSelect.LineSelect  = 'C3'
            LogSelect.paraname = 'Power Meter'
        }

        if(value === 'G2'){
            LogSelect.LineSelect  = 'G2'
            LogSelect.paraname = 'Total Energy'
        }
    }

    const EnergyLogRequest = async (DT) => {
        DTc3 = DT
        console.log(DTc3);
        C3DTRange = ConvertMonentToDateTime(DT)
        console.log(C3DTRange);

        let ParaRequest = [LogSelect.paraname]

        await UpdateTableData(LogValue['C3Selected'], C3DTRange[0], C3DTRange[1], 'Polymerlink', LogSelect.line, '1', "C3Table", C3ModelMap)
        console.log(LogValue["C3Table"]);
        // line.changeData(LogValue["C3Table"])
    }

    // const ChangeG2Select = (value) => {
    //     LogValue['G2Selected'] = value

    // }


    return (
        <div>
            <div style={{ paddingBottom: '10px' }}>
                <Divider orientation="left">Energy Consumption</Divider>
            </div>


            <Spin tip="Loading..." spinning={Loading} size="large">
                <Card title={'Energy'} bordered={true} style={{ width: '100%', borderRadius: 15, backgroundColor: '#ffffff' }} hoverable={true}>
                    <div id='container'></div>
                </Card>
            </Spin>,


            <div style={{ paddingBottom: '10px' }}>
                <Divider orientation="left">C3 LEnergy Log</Divider>
            </div>
            <div style={{ paddingBottom: '10px' }}>
                <RangePicker onOk={EnergyLogRequest} />
                <Button type='primary' style={{ marginLeft: '10px' }} onClick={() => { EnergyLogRequest(DTc3) }}>Update</Button>
            </div>

            {/* <Select
                mode="multiple"
                size="middle"
                placeholder="Please select"
                defaultValue={[EnergyOption[0]]}
                style={{ width: '100%', paddingBottom: '20px' }}
                onChange={ChangeC3Select}
            >
                {EnergyOption}
            </Select> */}

            <div style={{ paddingBottom: '20px' }}>
                <Select defaultValue="C3" style={{ width: 70 }} onChange={ChangeC3Select}>
                    <Option value="C3">C3</Option>
                    <Option value="G2">G2</Option>
                </Select>
            </div>

            <Card title='Log Value C3' bordered={true}
                style={{ width: '100%', borderRadius: 15, marginBottom: '10px' }} hoverable={true}>
                <Table bordered columns={columnsC3} dataSource={LogValue['C3Table']} size="small" scroll={{ y: 500 }} pagination={false} />
            </Card>

            <Card title={'C3 Graph Visulize'} bordered={true} style={{ width: '100%', borderRadius: 15, backgroundColor: '#ffffff' }} hoverable={true}>
                <div id='C3Graph'></div>
            </Card>

        </div>
    )
}

export default EnergyUsage
