import React from 'react'
import { Page, Text, View, Document, StyleSheet, ReactPDF, PDFDownloadLink } from '@react-pdf/renderer';
import { Button, Table, Tabs } from 'antd';
import html2canvas from 'html2canvas'
import jsPDF from 'jspdf'
import Svgexporttest from './Svgexporttest';

const columns = [
    {
        title: 'Full Name',
        width: 100,
        dataIndex: 'name',
        key: 'name',
        fixed: 'left',
    },
    {
        title: 'Age',
        width: 100,
        dataIndex: 'age',
        key: 'age',
        fixed: 'left',
    },
    {
        title: 'Column 1',
        dataIndex: 'address',
        key: '1',
        width: 150,
    },
    {
        title: 'Column 2',
        dataIndex: 'address',
        key: '2',
        width: 150,
    },
    {
        title: 'Column 3',
        dataIndex: 'address',
        key: '3',
        width: 150,
    },
    {
        title: 'Column 4',
        dataIndex: 'address',
        key: '4',
        width: 150,
    },
    {
        title: 'Column 5',
        dataIndex: 'address',
        key: '5',
        width: 150,
    },
    {
        title: 'Column 6',
        dataIndex: 'address',
        key: '6',
        width: 150,
    },
    {
        title: 'Column 7',
        dataIndex: 'address',
        key: '7',
        width: 150,
    },
    { title: 'Column 8', dataIndex: 'address', key: '8' },
    {
        title: 'Action',
        key: 'operation',
        fixed: 'right',
        width: 100,
        render: () => <a>action</a>,
    },
];

const data = [];
for (let i = 0; i < 100; i++) {
    data.push({
        key: i,
        name: `Edrward ${i}`,
        age: 32,
        address: `London Park no. ${i}`,
    });
}

const Testpage1 = () => {

    var generateData = function(amount) {
        var result = [];
        var data = {
          coin: "100",
          game_group: "GameGroup",
          game_name: "XPTO2",
          game_version: "25",
          machine: "20485861",
          vlt: "0"
        };
        for (var i = 0; i < amount; i += 1) {
          data.id = (i + 1).toString();
          result.push(Object.assign({}, data));
        }
        return result;
      };
      
      function createHeaders(keys) {
        var result = [];
        for (var i = 0; i < keys.length; i += 1) {
          result.push({
            id: keys[i],
            name: keys[i],
            prompt: keys[i],
            width: 65,
            align: "center",
            padding: 0
          });
        }
        return result;
      }
      
      var headers = createHeaders([
        "id",
        "coin",
        "game_group",
        "game_name",
        "game_version",
        "machine",
        "vlt"
      ]);
      
      var doc = new jsPDF({ putOnlyUsedFonts: true, orientation: "landscape" });
    //   doc.table(1, 1, generateData(100), headers, { autoSize: true });


    const convertCanvas = () => {
        const input = document.getElementById("capture");

        html2canvas(input, { scale: 6 })
            .then((canvas) => {
                const imgData = canvas.toDataURL("image/jpeg");
                const pdf = new jsPDF({
                    orientation: 'landscape',
                    type: 'a4'
                });
                //const imgProps = pdf.getImageProperties(imgData);
                const pdfWidth = pdf.internal.pageSize.getWidth();
                const pdfHeight = pdf.internal.pageSize.getHeight();
                pdf.addImage(imgData, 'SVG', 0, 0, pdfWidth, pdfHeight);
                pdf.save('download.pdf');
            });
    }


    return (
        <div>
            {/* <Button onClick={MyDocument}>
                capture
            </Button> */}
            {/* 
            <div id='capture' style={{ width: '100%', height: 600, borderRadius: 15, }}>
            </div> */}
            <div>
                {doc.table(1, 1, generateData(100), headers, { autoSize: true })}

            </div>

        </div>
    )
}


export default Testpage1
