import { Button, Col, Divider, Row, DatePicker, Card, Table, Select, Spin } from 'antd'
import React, { useState, useEffect } from 'react'
import { logdata_request, RealtimeData_Request } from '../../api'
import { C3Model, C3ModelMap, G2Model } from '../../DataRequest/DataModel'
import { ConvertMonentToDateTime, DataSortTime, GetDateTime } from '../../DataRequest/LogDataRequestWrap'
import { Line, Column } from '@antv/g2plot';
import moment from 'moment';
import { each, groupBy } from '@antv/util';




let DTinitial = GetDateTime(0, 1)  // get from yesterday 00:00 to today 00:00
let BlankPage = 1, DTc3 = [], DTg2 = []
let EnergyDTRange = [], G2DTRange = []
let MonthlyEnergyDataSet = []
let TotalEnergyInmonth = []

const annotations = [];


const monthNames = ["January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
];



const Dailydata = [
    {
        DateTime :'24/5/2022',
        type:'Line50kg',
        Value : 245,
    },
    {
        DateTime :'25/5/2022',
        type:'Line50kg',
        Value : 245,
    },
    {
        DateTime :'26/5/2022',
        type:'Line50kg',
        Value : 245,
    },
    {
        DateTime :'27/5/2022',
        type:'Line50kg',
        Value : 245,
    },
    {
        DateTime :'28/5/2022',
        type:'Line50kg',
        Value : 245,
    },
    {
        DateTime :'29/5/2022',
        type:'Line50kg',
        Value : 245,
    },
    {
        DateTime :'30/5/2022',
        type:'Line50kg',
        Value : 245,
    },


    {
        DateTime :'24/5/2022',
        type:'Line1kg',
        Value : 543,
    },
    {
        DateTime :'25/5/2022',
        type:'Line1kg',
        Value : 463,
    },
    {
        DateTime :'26/5/2022',
        type:'Line1kg',
        Value : 234,
    },
    {
        DateTime :'27/5/2022',
        type:'Line1kg',
        Value : 54,
    },
    {
        DateTime :'28/5/2022',
        type:'Line1kg',
        Value : 643,
    },
    {
        DateTime :'29/5/2022',
        type:'Line1kg',
        Value : 76,
    },
    {
        DateTime :'30/5/2022',
        type:'Line1kg',
        Value : 876,
    },


    {
        DateTime :'24/5/2022',
        type:'Line2kg',
        Value : 543,
    },
    {
        DateTime :'25/5/2022',
        type:'Line2kg',
        Value : 463,
    },
    {
        DateTime :'26/5/2022',
        type:'Line2kg',
        Value : 234,
    },
    {
        DateTime :'27/5/2022',
        type:'Line2kg',
        Value : 54,
    },
    {
        DateTime :'28/5/2022',
        type:'Line2kg',
        Value : 643,
    },
    {
        DateTime :'29/5/2022',
        type:'Line2kg',
        Value : 76,
    },
    {
        DateTime :'30/5/2022',
        type:'Line2kg',
        Value : 876,
    },

    
    {
        DateTime :'24/5/2022',
        type:'Line1Ton',
        Value : 564,
    },
    {
        DateTime :'25/5/2022',
        type:'Line1Ton',
        Value : 23,
    },
    {
        DateTime :'26/5/2022',
        type:'Line1Ton',
        Value : 64,
    },
    {
        DateTime :'27/5/2022',
        type:'Line1Ton',
        Value : 76,
    },
    {
        DateTime :'28/5/2022',
        type:'Line1Ton',
        Value : 23,
    },
    {
        DateTime :'29/5/2022',
        type:'Line1Ton',
        Value : 46,
    },
    {
        DateTime :'30/5/2022',
        type:'Line1Ton',
        Value : 86,
    },
]


DTc3.push(moment(DTinitial[2]))
DTc3.push(moment(DTinitial[3]))
DTg2 = DTc3


let LogValue = {
    'EnergyTable': [],
    "G2Table": [],
    "C3Graph": [],
    "G2Graph": [],
    "C3Selected": [],
    "G2Selected": [],
}

let LogSelect = {
    LineSelect: 'C3',
    paraname: 'Power Meter'
}

const EnergyOption = ['C3', 'G2']

const { RangePicker } = DatePicker;
const { Option } = Select;

let line, stackedColumnPlot

const EnergyUsage = () => {

    const [state, setState] = useState({ count: 0 });
    const [Loading, setLoading] = useState(false);

    useEffect(() => {
        console.log('Reffect');

        //  BarChartInit()

        stackedColumnPlot = new Column('container', {
            data: Dailydata,
            isStack: true,
            xField: 'DateTime',
            yField: 'Value',
            seriesField: 'type',
            label: {
                position: 'middle',
                layout: [
                    { type: 'interval-adjust-position' },
                    { type: 'interval-hide-overlap' },
                    { type: 'adjust-color' },
                ],
            },
            annotations,
        });

        try { stackedColumnPlot.render(); }
        catch { }

        return () => {
            stackedColumnPlot.destroy()
        }
    }, [])

    const UpdateTableData = async (Parameter, StartTime, EndTime, Site, Line, Page, BufferName, Model) => {
        console.log(Parameter)
        const response = await logdata_request(Parameter, StartTime, EndTime, Site, Line, Page)
        let data = DataSortTime(response)
        LogValue[BufferName] = data
        console.log(data);
        setState(({ count }) => ({ count: count + 1 }));
    }

    const BarChartInit = async () => {
        setLoading(true)
        // console.log(Parameter)

        stackedColumnPlot.changeData(Dailydata)
        setLoading(false)
        // setState(({ count }) => ({ count: count + 1 }));
    }

    const DailyLogRequest = async() =>{

    }


    return (
        <div>
            <div style={{ paddingBottom: '10px' }}>
                <Divider orientation="left">Daily Count Log</Divider>
            </div>


            <Spin tip="Loading..." spinning={Loading} size="large">
                <Card title={'Weekly Record'} bordered={true} style={{ width: '100%', borderRadius: 15, backgroundColor: '#ffffff' }} hoverable={true}>
                    <div id='container'></div>
                </Card>
            </Spin>


            <div style={{ paddingBottom: '10px' }}>
                <Divider orientation="left">Daily Log</Divider>
            </div>

            <div style={{ paddingBottom: '10px' }}>
                <RangePicker onChange={DailyLogRequest} />
                <Button type='primary' style={{ marginLeft: '10px' }} onClick={() => { DailyLogRequest(DTc3) }}>Update</Button>
            </div>

            <div style={{ paddingBottom: '20px' }}>
                <Select defaultValue="C3" style={{ width: 70 }} >
                    <Option value="Line50kg">Line50kg</Option>
                    <Option value="Line1kg">Line1kg</Option>
                    <Option value="Line2kg">Line2kg</Option>
                    <Option value="Line1Ton">Line1Ton</Option>
                </Select>
            </div>

            <Card title='Energy Log' bordered={true}
                style={{ width: '100%', borderRadius: 15, marginBottom: '10px' }} hoverable={true}>
                {/* <Table bordered columns={columnsC3} dataSource={LogValue['EnergyTable']} size="small" scroll={{ y: 500 }} pagination={false} /> */}
            </Card>
        </div>
    )
}

export default EnergyUsage
