import React from 'react'
import { Page, Text, View, Document, StyleSheet, ReactPDF, PDFDownloadLink } from '@react-pdf/renderer';
import { Button, Table, Tabs } from 'antd';
import html2canvas from 'html2canvas'
import jsPDF from 'jspdf'
import Svgexporttest from './Svgexporttest';

const columns = [
    {
        title: 'Full Name',
        width: 100,
        dataIndex: 'name',
        key: 'name',
        fixed: 'left',
    },
    {
        title: 'Age',
        width: 100,
        dataIndex: 'age',
        key: 'age',
        fixed: 'left',
    },
    {
        title: 'Column 1',
        dataIndex: 'address',
        key: '1',
        width: 150,
    },
    {
        title: 'Column 2',
        dataIndex: 'address',
        key: '2',
        width: 150,
    },
    {
        title: 'Column 3',
        dataIndex: 'address',
        key: '3',
        width: 150,
    },
    {
        title: 'Column 4',
        dataIndex: 'address',
        key: '4',
        width: 150,
    },
    {
        title: 'Column 5',
        dataIndex: 'address',
        key: '5',
        width: 150,
    },
    {
        title: 'Column 6',
        dataIndex: 'address',
        key: '6',
        width: 150,
    },
    {
        title: 'Column 7',
        dataIndex: 'address',
        key: '7',
        width: 150,
    },
    { title: 'Column 8', dataIndex: 'address', key: '8' },
    {
        title: 'Action',
        key: 'operation',
        fixed: 'right',
        width: 100,
        render: () => <a>action</a>,
    },
];

const data = [];
for (let i = 0; i < 100; i++) {
    data.push({
        key: i,
        name: `Edrward ${i}`,
        age: 32,
        address: `London Park no. ${i}`,
    });
}

const Testpage1 = () => {

    const styles = StyleSheet.create({
        page: {
            flexDirection: 'row',
            backgroundColor: '#E4E4E4',
            orientation: "landscape"
        },
        section: {
            margin: 10,
            padding: 10,
            flexGrow: 1
        }
    });

    // Create Document Component
    const MyDocument = () => (
        <Document>
            <Page size="A4" style={styles.page}>
                <View style={styles.section}>
                    <Text>Section #1</Text>
                    <Text>Section #1</Text>
                    <Text>Section #1</Text>
                    <Text>Section #1</Text>
                    <Text>Section #1</Text>
                    <Text>Section #1</Text>
                    
                </View>
                <View style={styles.section}>
                    <Text>Section #2</Text>
                </View>
            </Page>
        </Document>
    );

    const GenerateTablePDF = (Datalist) => {
        <React.Fragment>

        </React.Fragment>
    }

    const convertCanvas = () => {
        const input = document.getElementById("capture");

        html2canvas(input, { scale: 6 })
            .then((canvas) => {
                const imgData = canvas.toDataURL("image/jpeg");
                const pdf = new jsPDF({
                    orientation: 'landscape',
                    type: 'a4'
                });
                //const imgProps = pdf.getImageProperties(imgData);
                const pdfWidth = pdf.internal.pageSize.getWidth();
                const pdfHeight = pdf.internal.pageSize.getHeight();
                pdf.addImage(imgData, 'SVG', 0, 0, pdfWidth, pdfHeight);
                pdf.save('download.pdf');
            });
    }


    return (
        <div>
            <Button onClick={MyDocument}>
                capture
            </Button>

            <div id='capture' style={{ width: '100%', height: 600, borderRadius: 15, }}>
                <MyDocument />
            </div>

        </div>
    )
}


export default Testpage1
