import React, { useState, useEffect } from 'react'
import { logdata_request, ReadJob, WriteJob } from '../../api';
import { DataSortTime, LogDataMappingTime, TableColumbTimeAsRow, TableColumbLabelAsRow, LogDataMappingName, ReportSort, TableColumbJob } from '../../DataRequest/LogDataRequestWrap';
import { C3Model, G2Model, JobModel } from '../../DataRequest/DataModel';
import TimeTableWrapper from '../Report/TimeTableWrapper';
import NameTableWrapper from '../Report/NameTableWrapper';
import ReportWrapper from '../Report/ReportWrapper';
import { Button, message } from 'antd';
import ReportListWrapper from '../Report/ReportListWrapper';
import moment from 'moment';
import XlsxPopulate from 'xlsx-populate'


let LogValue = {
    "LogC3": [],
    "LogG2": [],
}

let ReportList = {
    "C3ReportList": [],
    "G2ReportList": []
}

let ReportInfoG2 = {}
let ReportInfoC3 = {}


let BlankPage

const ColumbC3 = TableColumbLabelAsRow(C3Model)
const ColumbG2 = TableColumbLabelAsRow(G2Model)

const Line1Report = () => {

    const [state, setState] = useState({ count: 0 });

    const DataRequestC3 = (row) => {

        let TimeRange = []

        TimeRange[0] = (moment(row['Start Time'], "DD-MM-YYYY HH:mm:ss").toISOString(false).split('.')[0] + "Z")
        TimeRange[1] = (moment(row['End Time'], "DD-MM-YYYY HH:mm:ss")).toISOString(false).split('.')[0] + "Z"

        ReportInfoC3.Info1 = row['Job No']
        ReportInfoC3.Info2 = row['Start Time']
        ReportInfoC3.Info3 = row['End Time']
        ReportInfoC3.Info4 = row['Color']
        ReportInfoC3.Info5 = row['Grade']
    }
    const ColumbJobC3 = TableColumbJob(JobModel, DataRequestC3)


    const DataRequestG2 = (row) => {

        // console.log(row)

        let TimeRange = []

        TimeRange[0] = (moment(row['Start Time'], "DD-MM-YYYY HH:mm:ss").toISOString(false).split('.')[0] + "Z")
        TimeRange[1] = (moment(row['End Time'], "DD-MM-YYYY HH:mm:ss")).toISOString(false).split('.')[0] + "Z"

        ReportInfoG2.Info1 = row['Job No']
        ReportInfoG2.Info2 = row['Start Time']
        ReportInfoG2.Info3 = row['End Time']
        ReportInfoG2.Info4 = row['Color']
        ReportInfoG2.Info5 = row['Grade']

        console.log(ReportInfoG2);


        UpdateTableDataG2([], TimeRange[0], TimeRange[1], 'Polymerlink', 'G2', BlankPage, 'LogG2', G2Model)
    }
    const ColumbJobG2 = TableColumbJob(JobModel, DataRequestG2)



    const UpdateTableDataC3 = async (Parameter, StartTime, EndTime, Site, Line, Page, BufferName, Model) => {
        const response = await logdata_request(Parameter, StartTime, EndTime, Site, Line, Page)
        //console.log(response);
        let data = DataSortTime(response)
        console.log(data);

        LogValue[BufferName] = LogDataMappingName(Model, data)
        console.log(LogValue[BufferName]);
        setState(({ count }) => ({ count: count + 1 }));
    }

    const UpdateTableDataG2 = async (Parameter, StartTime, EndTime, Site, Line, Page, BufferName, Model) => {
        const response = await logdata_request(Parameter, StartTime, EndTime, Site, Line, Page)
        let data = DataSortTime(response)
        console.log(data);

        LogValue[BufferName] = LogDataMappingName(Model, data)
        setState(({ count }) => ({ count: count + 1 }));
    }

    const CreateJob = async (info, datetimeStart, datetimeEnd, site, line) => {
        const response = await WriteJob(info, datetimeStart, datetimeEnd, site, line)
        if (response.success === true) {
            message.success('Create Success !')
        }
    }

    const RequestJob = async (info, datetimeStart, datetimeEnd, site, line, BufferName) => {
        const response = await ReadJob(info, datetimeStart, datetimeEnd, site, line)
        let data = ReportSort(response)
        ReportList[BufferName] = data
        //console.log(data);
        setState(({ count }) => ({ count: count + 1 }));
    }




    const WriteExcelCell = () => {

        fetch('ExtruderSheet.xlsx')
            .then(res => res.arrayBuffer())
            .then(buffer => {
                XlsxPopulate.fromDataAsync(buffer)
                    .then(workbook => {

                        // r = workbook.sheet(0).cell("A8").value("foo");
                        const r = workbook.sheet(0).range("A7:C9");

                        r.value([
                            [1, 2, 3],
                            [4, 5, 6],
                            [7, 8, 9]
                        ]);

                        workbook.outputAsync()
                            .then(function (blob) {
                                if (window.navigator && window.navigator.msSaveOrOpenBlob) {
                                    // If IE, you must uses a different method.
                                    window.navigator.msSaveOrOpenBlob(blob, "out.xlsx");
                                } else {
                                    var url = window.URL.createObjectURL(blob);
                                    var a = document.createElement("a");
                                    document.body.appendChild(a);
                                    a.href = url;
                                    a.download = "ExtruderSheet.xlsx";
                                    a.click();
                                    window.URL.revokeObjectURL(url);
                                    document.body.removeChild(a);
                                }
                            });
                    })
                    .then(data => {
                        // saveAs(new Blob([s2ab(data)], { type: "application/octet-stream" }), 'test.xlsx');
                        console.log(data);
                        // upload data to AWS S3
                    })
                    .catch(err => console.error(err));
            });
    }

    const SortToArray = (rawdata) => {

        const a = Object.values(LogValue['LogC3'])
        console.log(a);


    }


    return (
        <div>
            <Button onClick={WriteExcelCell}> write </Button>
            <Button onClick={SortToArray} > test </Button>

            {/* C3 part here */}
            <ReportWrapper CreateJobCallBack={CreateJob}
                site={'Polymerlink'} line={'C3'}
            />

            <ReportListWrapper ReportListCallBack={RequestJob}
                site={'Polymerlink'} line={'C3'}
                ReportList={ReportList['C3ReportList']}
                Columb={ColumbJobC3}
                BufferName={"C3ReportList"}
            />

            <TimeTableWrapper SetTimeRangeCallBack={UpdateTableDataC3}
                LogValue={LogValue['LogC3']} Columb={ColumbC3}
                site={'C3'} page={'1'} BufferName={"LogC3"} Model={C3Model}
                JobInfo={ReportInfoC3}
            />


            {/* G2 part here */}
            <ReportWrapper CreateJobCallBack={CreateJob}
                site={'Polymerlink'} line={'G2'}
            />

            <ReportListWrapper ReportListCallBack={RequestJob}
                site={'Polymerlink'} line={'G2'}
                ReportList={ReportList['G2ReportList']}
                Columb={ColumbJobG2}
                BufferName={"G2ReportList"}

            />

            <NameTableWrapper SetTimeRangeCallBack={UpdateTableDataG2}
                LogValue={LogValue['LogG2']} Columb={ColumbG2}
                site={'G2'} page={BlankPage} BufferName={"LogG2"} Model={G2Model}
                JobInfo={ReportInfoG2}
            />
        </div>
    )


}

export default Line1Report
